import React, {useState,useEffect} from 'react'
import CarByYear  from "./components/CarByYear";
function App() {

  const [state,setState] = useState("");
  const [infoYear, setinfoYear] = useState({});

  const handleChange =  e => setState(e.target.value)

  useEffect(() => {
    const onYearOption = async year => {
      const response = await fetch("http://localhost:3000/bestperyear/"+ year)
      const json= await response.json()
      setinfoYear(json)
    }

    onYearOption();
  });

  /*
  const onYearOption = async year => {
    const response = await fetch("http://localhost:3000/bestperyear/"+ year)
    const json= await response.json()
    setState(json.data)
  }*/

  return (
    <main>
      <h2>Cotizaci√≥n de autos</h2>
      <CarByYear onYearOption = {onYearOption} year= {state} handleChange = {handleChange}/>

    </main>
  );
}

export default App;
